<?php
 header("Content-type: application/json"); class getOnlineOperators{ static function getOperators(){ require 'mysql.php'; $sql = "SELECT ok_operators.operator_id, ok_operators.operator_name, ok_operators.operator_surname, ok_operators.operator_photo, ok_group.group_name AS operator_otdel FROM ok_operators, ok_group WHERE ok_operators.operator_online = '1' AND ok_group.group_id = ok_operators.operator_otdel"; $mysql = Mysql::getInstance(); $result = $mysql->query($sql); $operators = $result->fetchAll(PDO::FETCH_ASSOC); return json_encode($operators); } } session_start(); if($_SESSION['who']){ echo getOnlineOperators::getOperators(); } ?>
